function getJSON(callback){const xhttp=new XMLHttpRequest;xhttp.overrideMimeType("application/json"),xhttp.open("GET","build/groceries.json",!0),xhttp.onreadystatechange=function(){4==xhttp.readyState&&200==xhttp.status&&callback(xhttp.response)},xhttp.send(null)}getJSON(response=>{let listItem=JSON.parse(response).map(items=>`\n    <li class="groceryItem" tabindex="0">\n        <div class="qty">Qty: ${items.qty}</div>\n        <div class="middleContent">\n          <div class="item">${items.item}</div>\n          <hr/>\n          <div class=brandTypes> ${items.brand} ${items.type}</div>\n        </div>\n        <div class="productCategory">${items.category}</div>\n    </li>\n    `).join("");document.querySelector("#groceryListContainer").innerHTML=listItem});